#!/usr/bin/env python3

#Exercise 2
#Identify unique characters in the sequence
dna = ''
dna_unique = set(dna)

#Count the occurences of each unique character
nt_count = {}
for chr in dna_unique:
    nt_count[chr] = dna.count(chr)

print (f'Sequence:\n{dna}\nSequence length: {len(dna)}\nUnique characters in the sequence: {dna_unique}\nN. As: {nt_count['A']}\nN. Ts: {nt_count['T']}\nN. Gs: {nt_count['G']}\nN. Cs: {nt_count['C']}\nGC content: {(nt_count['C']+nt_count['G'])/len(dna):.2%}')
